return msg;

var metrics = flow.get("metrics")||{};


var net = global.get("config.networkId") || "local";

var oot = "";
//oot = 'stat_state {network="local", node="mynodered1", device="prometheus", vargroup="stat", varkey="state"} 1\n';

for (var m in metrics) {

    var tops = metrics[m].topic.split("/");

    if (isNaN(metrics[m].payload)) { continue; }
    if (tops[3] != msg.req.params.vargroup) { continue; }    


    oot += tops[3] + '_' + tops[4] + '{network="' + net + '",node="' + tops[1] + '",device="' + tops[2] + '",vargroup="' + tops[3] + '",varkey="' + tops[4] + '"} ' + metrics[m].payload + "\n";

}


msg.payload = oot;

return msg;

var metrics = flow.get("metrics")||{};


var net = global.get("config.networkId") || "local";

var oot = {readings:[]};
//oot = 'stat_state {network="local", node="mynodered1", device="prometheus", vargroup="stat", varkey="state"} 1\n';

for (var m in metrics) {

    var tops = metrics[m].topic.split("/");

    if (!isNaN(metrics[m].payload)) { metrics[m].payload = parseFloat(metrics[m].payload); }
    if (msg.req.params.vargroup && tops[3] != msg.req.params.vargroup) { continue; }    

    var tline = {};
    tline.value = metrics[m].payload;
    tline.timestamp = metrics[m].timestamp;
    tline.network = net;
    tline.node = tops[1];
    tline.device = tops[2];
    tline.vargroup = tops[3];
    tline.varkey = tops[4];

    oot.readings.push(tline);

    //oot += tops[3] + '_' + tops[4] + '{network="' + net + '",node="' + tops[1] + '",device="' + tops[2] + '",vargroup="' + tops[3] + '",varkey="' + tops[4] + '"} ' + metrics[m].payload + "\n";

}


msg.payload = oot;

return msg;

