// to suit your point format, run search/replace for '.x' and '.y';
// for 3D version, see 3d branch (configurability would draw significant performance overhead)

function scaleTime(p,direc) {

    if(direc==1) {

        p.timestamp = (timin - p.timestamp) / 10000;

    } else {

        p.timestamp = timin - (10000 * p.timestamp)  ;
    }

}
function scaleT(t) {


    return ((timin - t) / 10000)

}

// square distance between 2 points
function getSqDist(p1, p2) {



    var dx = scaleT(p1.timestamp) - scaleT(p2.timestamp),
        dy = p1.value - p2.value;

    return dx * dx + dy * dy;
}

// square distance from a point to a segment
function getSqSegDist(p, p1, p2) {

    var x = scaleT(p1.timestamp),
        y = p1.value,
        dx = scaleT(p2.timestamp) - x,
        dy = p2.value - y;

    if (dx !== 0 || dy !== 0) {

        var t = ((scaleT(p.timestamp) - x) * dx + (p.value - y) * dy) / (dx * dx + dy * dy);

        if (t > 1) {
            x = scaleT(p2.timestamp);
            y = p2.value;

        } else if (t > 0) {
            x += dx * t;
            y += dy * t;
        }
    }

    dx = scaleT(p.timestamp) - x;
    dy = p.value - y;

    return dx * dx + dy * dy;
}
// rest of the code doesn't care about point format



// simplification using optimized Douglas-Peucker algorithm with recursion elimination
function simplifyDouglasPeucker(points, sqTolerance) {

    var len = points.length,
        MarkerArray = typeof Uint8Array !== 'undefined' ? Uint8Array : Array,
        markers = new MarkerArray(len),
        first = 0,
        last = len - 1,
        stack = [],
        newPoints = [],
        i, maxSqDist, sqDist, index;

    markers[first] = markers[last] = 1;

    while (last) {

        maxSqDist = 0;

        for (i = first + 1; i < last; i++) {
            sqDist = getSqSegDist(points[i], points[first], points[last]);

            if (sqDist > maxSqDist) {
                index = i;
                maxSqDist = sqDist;
            }
        }

        if (maxSqDist > sqTolerance) {
            markers[index] = 1;
            stack.push(first, index, index, last);
        }

        last = stack.pop();
        first = stack.pop();
    }

    for (i = 0; i < len; i++) {
        if (markers[i]) {
            newPoints.push(points[i]);
        }
    }

    return newPoints;
}

// both algorithms combined for awesome performance
function simplify(points, tolerance, highestQuality) {

    var sqTolerance = tolerance !== undefined ? tolerance * tolerance : 1;

    //points = highestQuality ? points : simplifyRadialDist(points, sqTolerance);
    points = simplifyDouglasPeucker(points, sqTolerance);

    return points;
}

