var oot="";
var pL = msg.payload;
var cmdprefix = flow.get("cmdprefix") || "ti";

for (var p in pL) {


    if (pL[p].portId.substr(0,2)=="TH") {

        oot += 'echo "' + pL[p].portId + ',rthrd' + pL[p].channel + '",$('+cmdprefix+' rthrd ' + pL[p].channel + ')\n';

    } else if (pL[p].signalType.substr(0,5)=="PULSE") {

        oot += 'echo "' + pL[p].portId + ',countrd' + pL[p].channel + '",$('+cmdprefix+' countrd ' + pL[p].channel + ')\n';

    } else if (pL[p].portId.substr(0,1)=="D" && pL[p].signalType.substr(0,3)=="DRY") {

        oot += 'echo "' + pL[p].portId + ',contactrd' + pL[p].channel + '",$('+cmdprefix+' contactrd ' + pL[p].channel + ')\n';

    } else if (pL[p].portId.substr(0,1)=="P" && pL[p].signalType.substr(0,3)=="PWM") {

        oot += 'echo "' + pL[p].portId + ',pwmrd' + pL[p].channel + '",$('+cmdprefix+' pwmrd ' + pL[p].channel + ')\n';

    } else if (pL[p].portId.substr(0,2)=="IN" && pL[p].signalType.substr(0,5)=="NTC1K") {

        oot += 'echo "' + pL[p].portId + ',r1krd' + pL[p].channel + '",$('+cmdprefix+' r1krd ' + pL[p].channel + ')\n';

    } else if (pL[p].portId.substr(0,2)=="IN" && pL[p].signalType.substr(0,6)=="NTC10K") {

        oot += 'echo "' + pL[p].portId + ',r10krd' + pL[p].channel + '",$('+cmdprefix+' r10krd ' + pL[p].channel + ')\n';

    } else if (pL[p].portId.substr(0,2)=="IN" && pL[p].signalType.substr(0,5)=="0-10V") {

        oot += 'echo "' + pL[p].portId + ',adcrd' + pL[p].channel + '",$('+cmdprefix+' adcrd ' + pL[p].channel + ')\n';

    } else if (pL[p].portId.substr(0,2)=="IN" && pL[p].signalType.substr(0,3)=="DRY") {

        oot += 'echo "' + pL[p].portId + ',contactrd' + pL[p].channel + '",$('+cmdprefix+' contactrd ' + pL[p].channel + ')\n';

    } else if (pL[p].portId.substr(0,1)=="IN" && pL[p].signalType.substr(0,5)=="PULSE") {

        oot += 'echo "' + pL[p].portId + ',countrd' + pL[p].channel + '",$('+cmdprefix+' countrd ' + pL[p].channel + ')\n';

    }



}


msg.payload=oot;


return msg;

