var iface = msg.payload.split(":")[0]

if (iface.indexOf("usb") !== 0 ) { return null; }
flow.set("iface",iface.substring(0,4));


var lines = msg.payload.split("\n");

var direc,ltype;

var oot = [];

var megabytesTotal = 0



for (var l in lines) {

    direc="";
    ltype="";
    var ootl = {};

    lines[l]=lines[l].trim(); 

    if (lines[l].indexOf("RX")>-1) { direc = "RX"; }
    else if (lines[l].indexOf("TX")>-1) { direc = "TX"; }

    if (direc=="") { continue; }

    if (lines[l].indexOf("bytes")>-1) { 

        ltype = "megabytes"; 
        ootl.payload = parseFloat(lines[l].split("bytes ")[1].split(" ")[0]) / 1000000;
        megabytesTotal = megabytesTotal + ootl.payload;

    }
    else if (lines[l].indexOf("errors")>-1) { 

        ltype = "errors"; 
        ootl.payload = parseFloat(lines[l].split("errors ")[1].split(" ")[0]);
    }

    if (ltype=="") { continue; }


    ootl.topic = "modem/" + ltype + direc;
    oot.push(ootl);


}

oot.push({"topic":"modem/megabytesTotal", "payload":megabytesTotal});

var megabytesPM = parseInt(1000000 * (megabytesTotal - (flow.get("mblast") || megabytesTotal))) / 1000000;
flow.set("mblast",megabytesTotal);
oot.push({"topic":"modem/megabytesPM", "payload":megabytesPM});
oot.push({"topic":"modem/kbps", "payload":parseInt(1000000 * megabytesPM / 60)/1000});

msg.payload = oot;

return msg;

flow.set("lastUSBin", new Date().getTime());

var oot = [];

if (msg.payload.indexOf("AT+CIMI")>-1) {

    //msg.topic = "modem/simIMSI";
    //msg.payload = msg.payload.split("AT+CIMI")[1].split("\n")[1].trim();
    oot.push({"topic":"modem/simIMSI", "payload":msg.payload.split("AT+CIMI")[1].split("\n")[1].trim()});
}

else if (msg.payload.indexOf("+CPSI:")>-1) {

    //<System Mode>,<Operation Mode>,<MCC>-<MNC>,<TAC>,
    //<SCellID>,<PCellID>,<Frequency Band>,<earfcn>,<dlbw>,
    //<ulbw>,<RSRQ>,<RSRP>,<RSSI>,<RSSNR>

    if (msg.payload.indexOf("LTE")>-1) {

        flow.set("online","1");

        var flds = msg.payload.split("CPSI:")[1].trim().split(",");

        oot.push({"topic":"modem/sysMode", "payload":flds[0]});
        oot.push({"topic":"modem/opMode", "payload":flds[1]});
        oot.push({"topic":"modem/MCC-MNC", "payload":flds[2]});
        oot.push({"topic":"modem/TAC", "payload":flds[3]});
        oot.push({"topic":"modem/SCellID", "payload":flds[4]});
        oot.push({"topic":"modem/PCellID", "payload":flds[5]});
        oot.push({"topic":"modem/frqBand", "payload":flds[6]});
        oot.push({"topic":"modem/earfcn", "payload":flds[7]});
        oot.push({"topic":"modem/dlbw", "payload":flds[8]});
        oot.push({"topic":"modem/ulbw", "payload":flds[9]});
        oot.push({"topic":"modem/RSRQ", "payload":flds[10]});
        oot.push({"topic":"modem/RSRP", "payload":parseInt(flds[11])/10});
        oot.push({"topic":"modem/RSSI", "payload":flds[12]});
        oot.push({"topic":"modem/RSSNR", "payload":flds[13].split("\r")[0]});

        var xpSignal = parseInt(140 + (parseInt(flds[11])/10));

        oot.push({"topic":"system/signalGSM", "payload":xpSignal});


    }
    else { flow.set("online","0"); }
}

if (oot.length<1) { return null; }

msg.payload = oot;

return msg;

var lastUSBin = flow.get("lastUSBin") || 0;

var timin = new Date().getTime();




var age = (timin - lastUSBin)/1000;

msg.topic = "alarm/modemUSB";
msg.title = "Modem USB serial connection";
if (age < 90) { msg.payload = "ok"; }
else { msg.payload = "No modem response"; }


var msg2 = {};
msg2.topic = "alarm/modemLTE";
msg2.title = "Modem LTE 4G connection";

if (msg.payload !== "ok") { 

    msg2.payload = "No modem response";  

    var sysage = (timin - flow.get("starttime"))/1000;
    if (sysage < 900) {
        return [msg,msg2,null];

    } else {
        msg2.topic = "alarm/modemLTE";
        msg2.payload = "Rebooting System";
        return [msg,msg2,msg2];
    } 
}

else if (flow.get("online")=="0") { msg2.payload = "No LTE connection"; }

else { msg2.payload = "ok"; global.set("rebooting",null); }

return [msg,msg2,null];

msg.topic = "set/resetModemCount";
msg.title = "No Internet Reset Modem Counter";

msg.payload = global.get("settings.resetModemCount.value") || 0;

msg.payload = parseInt(msg.payload) + 1;


return msg;
