func:
if (!msg.payload.topic || msg.payload.topic == "none") { return null; }

flow.set("registers.r" + msg.payload.rAddress, msg.payload);

msg.payload = { 'fc': 3, 'unitid': 1, 'address': msg.payload.rAddress, 'quantity': msg.payload.rLength }

return msg;

//msg.payload = (msg.payload[0] * 256) + msg.payload[1];

if (!msg.modbusRequest) { return null; }

var rinfo = flow.get("registers.r" + msg.modbusRequest.address);

if (!rinfo.topic || rinfo.topic == "none") { return null; }
msg.topic = "modem/dat/" + rinfo.topic;
//msg.units = "s";

if (rinfo.dType == "32 bit unsigned integer") {
    msg.payload = parseFloat(msg.payload.buffer.readUIntBE(0,4));
}
else if (rinfo.dType == "16 bit unsigned integer") {
    msg.payload = parseFloat(msg.payload.buffer.readUIntBE(0, 2));
}
else if (rinfo.dType == "8 bit unsigned integer") {
    msg.payload = parseFloat(msg.payload.buffer.readUIntBE(0, 1));
}
else if (rinfo.dType == "32 bit integer") {
    msg.payload = parseFloat(msg.payload.buffer.readIntBE(0, 4));
}

else if (rinfo.dType == "Text") {
    msg.payload = msg.payload.buffer.toString('utf8');
}
else { return null; }


if (rinfo.topic == "systemTemperature ") {
    msg.payload = msg.payload/10;
}


return msg;
